// Longest Palindromic Substring

// Given a string s, find the longest palindromic substring in s. You may assume that the maximum length of s is 1000.

// Example 1:

// Input: "babad"
// Output: "bab"
// Note: "aba" is also a valid answer.
// Example 2:

// Input: "cbbd"
// Output: "bb"

/**
 * @param {string} s
 * @return {string}
 */
var longestPalindrome = function (s) {
  for (let count = s.length; count > 0; count--) {
    for (let index = 0; index <= s.length - count; index++) {
      let sub = s.substring(index, index + count)
      if(isPalindrome(sub)) {
        return sub
      }
    }
  }
  return ''
}

/**
 * @param {string} s
 * @return {bool}
 */
function isPalindrome(s) {
  if (s.length <= 1) {
    return true
  }
  const left = s.substring(0, Math.floor(s.length / 2))
  const right = s.substring(Math.ceil(s.length / 2))
  return left == right.split('').reverse().join('')
}


const s =
  ''
const result = longestPalindrome(s)

console.log(`${result}`)
